package ar.edu.itba.geb.compiler.java.syntaxtree;import ar.edu.itba.geb.compiler.java.visitors.TypeVisitor;import ar.edu.itba.geb.compiler.java.visitors.Visitor;public class ArrayAssign implements Statement {  public Identifier arrayId;  public Exp indexExpr, valueExpr;  public ArrayAssign(Identifier ai, Exp ae1, Exp ae2) {    arrayId = ai;    indexExpr = ae1;    valueExpr = ae2;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}