package ar.edu.itba.geb.compiler.java.syntaxtree;//import ar.edu.itba.geb.compiler.java.String;import ar.edu.itba.geb.compiler.java.visitors.TypeVisitor;import ar.edu.itba.geb.compiler.java.visitors.Visitor;public class Identifier extends Exp {  public String name;  private String tok;  public Identifier(Identifier i) {    this(i.name, i.tok);  }    public Identifier(String as, String token) {    name = as;    tok = token;  }  public String getToken() {    return tok;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }    @Override  public String toString() {    return name;  }  public String getDescriptor() {    return name + ":" + tok.toString();  }}