package ar.edu.itba.geb.compiler.java.syntaxtree;import ar.edu.itba.geb.compiler.java.visitors.TypeVisitor;import ar.edu.itba.geb.compiler.java.visitors.Visitor;public class If implements Statement {  public Exp boolExpr;  public StatementList trueStmt, falseStmt;  public If(Exp ae, StatementList as1, StatementList as2) {    boolExpr = ae;    trueStmt = as1;    falseStmt = as2;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}