package ar.edu.itba.geb.compiler.java.syntaxtree;import ar.edu.itba.geb.compiler.java.visitors.TypeVisitor;import ar.edu.itba.geb.compiler.java.visitors.Visitor;public class MainClass {  public Identifier classNameId, argId;  public VarDeclList localVars, classVars;  public MethodDeclList classMethods;  public StatementList statements;  public MainClass(          Identifier ai1, Identifier ai2, VarDeclList vl, StatementList stl, VarDeclList cv, MethodDeclList cm) {    classNameId = ai1;    argId = ai2;    statements = stl;    localVars = vl;    classVars = cv;    classMethods = cm;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}