package ar.edu.itba.geb.compiler.java.syntaxtree;import ar.edu.itba.geb.compiler.java.visitors.TypeVisitor;import ar.edu.itba.geb.compiler.java.visitors.Visitor;public class VariableOrMethod {	public VarDeclList varList;	public MethodDeclList metList;	public VariableOrMethod() {		varList = new VarDeclList();		metList = new MethodDeclList();	}	public void addVarDeclList(VarDeclList vdl) {		for (VarDecl vd : vdl.getList()) {			varList.addElement(vd);		}	}	public void addMethodDeclList(MethodDeclList mdl) {		for (MethodDecl md : mdl.getList()) {			metList.addElement(md);		}	}		public void accept(Visitor v) {		v.visit(this);	}	public Type accept(TypeVisitor v) {		return v.visit(this);	}}